mapper.insert:
INSERT INTO departments(
					Department_id
					, First_name
					, department_name
					, location_id

					, manager_id

					) VALUES(
					#{department_id}
					, #{department_name}
					, #{location_id}

					, #{manager_id}

					)

mapper.update:
UPDATE departments 

	 				 manager_id = #{manager_id}
	 			 , location_id = #{location_id}
	 		 , Department_name = #{department_name}

WHERE Department_id = #{department_id}
