mapper.select:
SELECT e.Employee_id , e.First_name , e.Last_name , e.Email , e.Hire_date , e.job_id , e.Salary , e.Manager_id , e.Department_id , e.Phone_number , d.department_name , j.job_title FROM employees e, departments d, jobs j WHERE e.department_id = d.department_id AND e.job_id = j.job_id
department_id = #{department_id}
AND job_id = #{job_id}
AND upper(first_name) LIKE '%'||upper(#{first_name})||'%'
ORDER BY Hire_date DESC

mapper.insert:
INSERT INTO employees(
					employee_id
					, First_name
					, last_name
					, email
					, hire_date
					, job_id
					, Phone_number
					, Department_id

					, manager_id

					) VALUES(
					#{employee_id}
					, #{first_name}
					, #{last_name}
					, #{email}
					, #{hire_date}
					, #{job_id}
					, #{phone_number}
					, #{department_id}

					, #{manager_id}

					)

mapper.update:
UPDATE employees 

	 			 First_name = #{first_name}
	 			 , last_name = #{last_name}
	 				 , email = #{email}
	 			 , hire_date = #{hire_date}
	 		 , Phone_number = #{phone_number}
	 				 , job_id = #{job_id}
	 		 , Department_id = #{department_id}

WHERE employee_id = #{employee_id}
